import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const App = () => {
  const [city, setCity] = useState('San Francisco');
  const [inputCity, setInputCity] = useState('San Francisco');
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchWeatherAndForecast = async () => {
    setLoading(true);
    setError(null);
    setWeather(null);
    setForecast(null);

    // Fetch data from our backend API
    const backendUrl = `http://localhost:5001/api/weather/${city}`; // Updated to use port 5001

    try {
      const response = await fetch(backendUrl);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch data from backend.');
      }
      const data = await response.json();

      // Update state with data from the backend
      setWeather(data.weather);
      setForecast(data.forecast);

    } catch (err) {
      console.error(err);
      setError(err.message || 'An error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWeatherAndForecast();
  }, [city]);

  const handleSearch = () => {
    if (inputCity.trim() !== '') {
      setCity(inputCity);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const getWeatherIcon = (description) => {
    if (!description) return 'üå°Ô∏è';
    const desc = description.toLowerCase();
    if (desc.includes('clear')) return '‚òÄÔ∏è';
    if (desc.includes('cloud')) return '‚òÅÔ∏è';
    if (desc.includes('rain')) return 'üåßÔ∏è';
    if (desc.includes('drizzle')) return 'üå¶Ô∏è';
    if (desc.includes('thunderstorm')) return '‚õàÔ∏è';
    if (desc.includes('snow')) return '‚ùÑÔ∏è';
    if (desc.includes('mist') || desc.includes('fog') || desc.includes('haze')) return 'üå´Ô∏è';
    return 'üå°Ô∏è';
  };

  return (
    <div className="flex flex-col items-center p-4 sm:p-8 min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100 font-sans">
      <div className="w-full max-w-4xl p-6 md:p-8 rounded-2xl shadow-xl bg-white dark:bg-gray-800 transition-colors duration-300">
        {/* Main Dashboard Header */}
        <h1 className="text-3xl sm:text-4xl font-bold text-center mb-6">Weather Dashboard</h1>

        {/* Location Search Input */}
        <div className="flex justify-center mb-8">
          <input
            type="text"
            value={inputCity}
            onChange={(e) => setInputCity(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Enter a city name..."
            className="p-3 w-64 md:w-80 rounded-l-xl border-2 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100"
          />
          <button
            onClick={handleSearch}
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-r-xl transition-colors duration-200"
          >
            Search
          </button>
        </div>

        {/* Loading and Error States */}
        {loading && (
          <div className="flex items-center justify-center py-10">
            <div className="text-xl font-medium text-gray-700 dark:text-gray-300">Loading...</div>
          </div>
        )}
        {error && (
          <div className="p-6 rounded-xl shadow-lg bg-red-100 text-red-700 text-center mb-8">
            <p>{error}</p>
          </div>
        )}

        {/* Current Weather Section */}
        {weather && (
          <div className="text-center mb-8 p-6 bg-gray-50 dark:bg-gray-700 rounded-xl shadow-inner transition-colors duration-300">
            <h2 className="text-2xl font-semibold mb-2">Current Weather in {weather.location}</h2>
            <div className="flex items-center justify-center mb-4">
              <span className="text-6xl mr-4">{getWeatherIcon(weather.description)}</span>
              <span className="text-7xl font-light">{weather.temperature}¬∞C</span>
            </div>
            <p className="text-xl font-medium capitalize mb-2">{weather.description}</p>
            <div className="flex flex-wrap justify-center gap-4 text-sm text-gray-600 dark:text-gray-300">
              <p>Humidity: <span className="font-semibold">{weather.humidity}%</span></p>
              <p>Wind: <span className="font-semibold">{weather.wind_speed} km/h</span></p>
              <p>Pressure: <span className="font-semibold">{weather.pressure} hPa</span></p>
            </div>
          </div>
        )}

        {/* Forecast Chart Section */}
        {forecast && (
          <div className="p-6 bg-gray-50 dark:bg-gray-700 rounded-xl shadow-inner transition-colors duration-300">
            <h2 className="text-2xl font-semibold mb-6 text-center">7-Day Temperature Forecast</h2>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart
                data={forecast}
                margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
              >
                <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                <XAxis dataKey="day" stroke="#6b7280" />
                <YAxis stroke="#6b7280" domain={['auto', 'auto']} />
                <Tooltip
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.9)',
                    borderColor: '#cbd5e1',
                    borderRadius: '10px',
                    color: '#1f2937'
                  }}
                  labelStyle={{ fontWeight: 'bold' }}
                  formatter={(value) => `${value}¬∞C`}
                />
                <Line
                  type="monotone"
                  dataKey="temp"
                  stroke="#3b82f6"
                  activeDot={{ r: 8 }}
                  strokeWidth={2}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
